mapper.select:
select a.no as productNo, a.title as title, a.image as image, a.price as price, a.description as description from product a join product_category b on a.product_category_no = b.no join product_manage c on a.product_manage_no = c.no where (b.large_name=#{category} or b.mid_name=#{category} or b.small_name=#{category}) and isdisplay=true
and now() < end_date
order by reg_date desc limit #{startIndex}, #{size};

select a.no as productNo, a.title as title, a.image as image, a.price as price, a.description as description from product a join product_category b on a.product_category_no = b.no join product_manage c on a.product_manage_no = c.no where (b.large_name like concat('%',#{keyword},'%') or b.mid_name like concat('%',#{keyword},'%') or b.small_name like concat('%',#{keyword},'%') or a.title like concat('%',#{keyword},'%')) and isdisplay=true
and now() < end_date
order by reg_date desc limit #{startIndex}, #{size};
