mapper.insert:
insert into product values(default, #{productCategoryNo}, (select no from product_manage where isdisplay=#{isdisplay} and issell=#{issell} and isdisplay_main=#{isdisplayMain}), #{title}, #{image}, #{price}, #{mileageAdd}, #{description}, #{descriptionDetail}, #{shippingPrice}, now(), #{endDate});
select last_insert_id() as productNo;
insert into product_image values
(default, #{productNo}, #{image.imageDetail}, #{image.imageTitle}, #{image.imageDescription})
insert into product_option(product_no, name) select #{productNo}, #{option.optionName} from dual where not exists(select no from product_option where product_no=#{productNo} and name=#{option.optionName});
insert into product_option_detail values(default, (select max(no) from product_option), #{option.optionValue});
insert into product_quantity values
(default, #{productNo}, #{quantity.optionCode}, #{quantity.realQuantity}, #{quantity.availableQuantity})

mapper.select:
select a.no as productNo, a.title as title, a.image as image, a.price as price, a.reg_date as regDate, a.end_date as endDate, b.isdisplay as isdisplay, b.issell as issell, b.isdisplay_main as isdisplayMain, c.name as category, d.name as optionName, e.value as optionValue from product a join product_manage b on a.product_manage_no=b.no join product_category c on a.product_category_no=c.no join product_option d on a.no=d.product_no join product_option_detail e on d.no=e.option_no where b.isdisplay=#{isdisplay} and b.issell=#{issell} and b.isdisplay_main=#{isdisplayMain}
and a.title like concat('%',#{productName},'%')
and c.name like concat('%',#{categoryName},'%')
and DATE(a.reg_date) >= #{regDateStart}
and DATE(a.reg_date) <= #{regDateEnd}
order by a.reg_date desc

mapper.update:
update product set title=#{title}, image=#{image}, price=#{price}, mileage_add=#{mileageAdd}, description=#{description}, description_detail=#{descriptionDetail}, shipping_price=#{shippingPrice}, reg_date=now(), end_date=#{endDate}, product_category_no=#{productCategoryNo}, product_manage_no=(select no from product_manage where isdisplay=#{isdisplay} and issell=#{issell} and isdisplay_main=#{isdisplayMain}) where no=#{productNo};
delete from product_image where product_no=#{productNo}; insert into product_image values
(default, #{productNo}, #{image.imageDetail}, #{image.imageTitle}, #{image.imageDescription})
insert into product_option(product_no, name) select #{productNo}, #{option.optionName} from dual where not exists(select no from product_option where product_no=#{productNo} and name=#{option.optionName});
insert into product_option_detail values(default, (select max(no) from product_option), #{option.optionValue});
delete from product_quantity where product_no=#{productNo}; insert into product_quantity values
(default, #{productNo}, #{quantity.optionCode}, #{quantity.realQuantity}, #{quantity.availableQuantity})

update product_category set
name=#{categoryName}
where no=#{categoryNo}

mapper.delete:
update product set
product_manage_no=(select no from product_manage
					where isdisplay=false and issell=false and isdisplay_main=false)
where product_category_no=#{categoryNo};

delete from product_category
where no=#{categoryNo}

update product set product_manage_no=(select no from product_manage where isdisplay=false and issell=false and isdisplay_main=false) where
product_category_no=#{categoryNo}
delete from product_category where
no=#{categoryNo2}
